import './style.scss';

import clsx from 'clsx';
import { useId, useMemo } from 'react';

import { useI18nContext } from '../../../../../../i18n/i18n-react';
import { DeviceSetupMethod } from '../../types';

type Props = {
  methodType: DeviceSetupMethod;
  active: boolean;
  onClick: () => void;
};

export const DeviceSetupMethodCard = ({ methodType, active, onClick }: Props) => {
  const { LL } = useI18nContext();
  const localLL = LL.addDevicePage.steps.setupMethod.methods;

  const [title, description] = useMemo(() => {
    switch (methodType) {
      case DeviceSetupMethod.DESKTOP_CLIENT:
        return [localLL.desktop.title(), localLL.desktop.description()];
      case DeviceSetupMethod.NATIVE_WG:
        return [localLL.mobile.title(), localLL.mobile.description()];
      default:
        throw Error('Unimplemented setup method supplied to method card.');
    }
  }, [localLL.desktop, localLL.mobile, methodType]);

  return (
    <div
      className={clsx('device-method-card', {
        active,
      })}
      data-testid={`add-device-method-${methodType.valueOf()}`}
      data-active={active}
      onClick={onClick}
    >
      <p className="title">{title}</p>
      <p className="description">{description}</p>
      {methodType === DeviceSetupMethod.DESKTOP_CLIENT && (
        <>
          <div className="icon">
            <PhoneSvg />
            <DesktopSvg />
          </div>
          <div className="icon">
            <DefguardLogo />
          </div>
        </>
      )}
      {methodType === DeviceSetupMethod.NATIVE_WG && (
        <div className="wg-icon">
          <WireguardLogo />
        </div>
      )}
    </div>
  );
};

